            <thead>
              <tr>
                <th>Project</th>
                <th>Date</th>
                <th class="numeric">Orders</th>
                <th class="numeric">Total Sales</th>
                <th class="numeric">Avg. Order Value</th>
              </tr>
            </thead>
            <tbody>
             {{ $json.tableRows }}
            </tbody>
          </table>
        </div>

        <!-- Summary -->
        <div class="summary" role="note" aria-label="summary">

        <table role="presentation" cellspacing="0" cellpadding="0" border="0" width="100%" style="border-collapse:collapse; font-size:14px; color:#23303e;">
  <tr>
    <td width="33.33%" valign="top" style="padding:8px; border-right:1px solid #e6eef6;">
      <div style="color:#6b7886; font-size:13px;">Total Orders</div>
      <div style="font-weight:700; font-size:16px;">{{$node['Calculate All Projects Sales Data'].json['Total Orders']}}</div>
    </td>
    <td width="33.33%" valign="top" style="padding:8px; border-right:1px solid #e6eef6;">
      <div style="color:#6b7886; font-size:13px;">Total Sales</div>
      <div style="font-weight:700; font-size:16px;">{{$node['Calculate All Projects Sales Data'].json['Total Sales']}}</div>
    </td>
    <td width="33.33%" valign="top" style="padding:8px;">
      <div style="color:#6b7886; font-size:13px;">Overall Avg. Order Value</div>
      <div style="font-weight:700; font-size:16px;">{{$node['Calculate All Projects Sales Data'].json['Overall Avg. Order Value']}}</div>
    </td>
  </tr>
</table> 

        </div>

      </div>

      <!-- Footer -->
      <div class="email-footer" role="contentinfo">
        <div>Report generated automatically â€” Shopware Projects</div>
        <div style="margin-top:8px;">Please do not reply to this email.</div>
      </div>

    </div>
  </div>
</body>
</html>

