parameters.jsCode:
const formatter = new Intl.NumberFormat('de-DE', {
  style: 'currency',
  currency: 'EUR'
});

const tableRows = $node['Prepare Projects Data'].json.projectsData.map(p => (
              `<tr>
                <td><strong>${p.Project}</strong></td>
                <td class="muted">${p.Date}</td>
                <td class="numeric">${p.Orders}</td>
                <td class="numeric">${formatter.format(p['Total Sales'])}</td>
                <td class="numeric">${formatter.format(p['Avg. Order Value'])}</td>
              </tr>`
                ))

return [{tableRows: tableRows.join('')}]

const formatter = new Intl.NumberFormat('de-DE', {
  style: 'currency',
  currency: 'EUR'
});

const totalOrders = $node['Prepare Projects Data'].json.projectsData.reduce(
  (accumulator, object) => accumulator + object.Orders,
  0,
);

const overallTotal = $node['Prepare Projects Data'].json.projectsData.reduce(
  (accumulator, object) => accumulator + object['Total Sales'],
  0,
);

const overallAvgOrderValue = overallTotal / $node['Prepare Projects Data'].json.projectsData.length;

return [{
         Date: new Date().toISOString().split('T')[0],
         ['Total Orders']: totalOrders,
         ['Total Sales']:  formatter.format(overallTotal),
         ['Overall Avg. Order Value']: formatter.format(overallAvgOrderValue || 0)
}]

const formatter = new Intl.NumberFormat('de-DE', {
  style: 'currency',
  currency: 'EUR'
});

const sections = $node['Prepare Projects Data'].json.projectsData.map(p => (
            {
			"type": "section",
			"fields": [
				{
					"type": "mrkdwn",
					"text": `*Project ${p.Project} â€“ Orders:*\n${p.Orders}`
				},
				{
					"type": "mrkdwn",
					"text": `*Sales:*\n${formatter.format(p['Total Sales'])}`
				},
				{
					"type": "mrkdwn",
					"text": `*Avg. Order Value:*\n${formatter.format(p['Avg. Order Value'])}`
				}
			]
		}
                ))

return [{sections}]
